export default new VueRouter({
  routes : [
    {
      path: '/',
      name: 'Home',
      component: Home
    },
    {
      path: '/Forum',
      name: 'Forum',
      // route level code-splitting
      // this generates a separate chunk (about.[hash].js) for this route
      // which is lazy-loaded when the route is visited.
      component: Forum
    },
    {
      path: '/Login',
      name: 'Login',
      // route level code-splitting
      // this generates a separate chunk (about.[hash].js) for this route
      // which is lazy-loaded when the route is visited.
      component: Connection
    },
    {
      path: '/Account',
      name: 'MyAccount',
      // route level code-splitting
      // this generates a separate chunk (about.[hash].js) for this route
      // which is lazy-loaded when the route is visited.
      component: Account
    },
    {
      path: '/Utilisateurs',
      name: 'Utilisateurs',
      // route level code-splitting
      // this generates a separate chunk (about.[hash].js) for this route
      // which is lazy-loaded when the route is visited.
      component: Groups
    }
  ]
})
